    private static int singleNumber(int[] ints) {
        List<Integer> single = new ArrayList<>();
        List<Integer> removed = new ArrayList<>();
        for (int num : ints) {
            if (!removed.contains(num)) {
                if (single.contains(num)) {
                    removed.add(num);
                    single.remove(single.indexOf(num));
                } else single.add(num);
            }
        }

        return single.get(single.size() - 1);
    }
